function colorText(input, style = "0") {
    return `\x1b[${style}m${input}\x1b[0m`;
}

const colorConsole = {
    log: console.log,
    info: console.info,
    warn: (text) => {
        console.warn(colorText(text, "7;93"));
    },
    error: (text) => {
        console.error(colorText(text, "91"));
    }
}
exports.colorConsole = colorConsole;